import Webcam from "react-webcam";
import { useCallback } from "react";
import { useState } from "react";
import React from "react";
import './WebcamCapture.css'


const FACING_MODE_USER = "user";
const FACING_MODE_ENVIRONMENT = "environment";

const videoConstraints = {
  facingMode: FACING_MODE_USER
};

export const WebcamCapture = (props) => {
  const [facingMode, setFacingMode] = React.useState(FACING_MODE_USER);
  const[style,setstyle]=useState("basic")
  

  const handleClick = React.useCallback(() => {
    setFacingMode(
      prevState =>
        prevState === FACING_MODE_USER
          ? FACING_MODE_ENVIRONMENT
          : FACING_MODE_USER
    );
  }, []);
  
  const webcamRef = React.useRef(null);

  const [val,setval] = useState(false);
  const[imageUrl,setimageUrl]=useState("");
  const handleCapture = () =>{

    const imageSrc = webcamRef.current.getScreenshot();
    setimageUrl(imageSrc);
     setval(!val)
  }

   const handleContrast = () =>{
    setstyle("bright")
    console.log("ndjhf");
   }

   const handlePdf = () => {
    props.setpdf(true);
   }
  return (
    <> 
    { val==false?

    <div>
      <Webcam
        audio={false}
        screenshotFormat="image/jpeg"
        ref={webcamRef}
        videoConstraints={{
          ...videoConstraints,
          facingMode
        }}
      />
      </div>


      :
      <div><img className={style} src={imageUrl}/></div>
      
}
      <button onClick={handleClick}>Switch camera</button>
      <button onClick={handleCapture}>Capturgjhge</button>
      { val==true?
      <button onClick={handleContrast}>contrast</button>:
      <div></div>
}
{ val==true?
      <button onClick={handlePdf}>Save As Pdf</button>:
      <div></div>
}
    </>
  );
};
