import React, { useRef } from 'react';
import html2pdf from "html2pdf.js";
import './pdf.css';
import ima from './images/dummy.jpg'


const Pdf = (props) => {
  const contentRef = useRef(); 

  const exportHTMLtoPDF = () => {
    const element = contentRef.current; // Access the component using the ref
    
    // Wait for the image to load before generating the PDF
    const imgElement = element.querySelector('img');
    if (imgElement.complete) {
      // If the image is already loaded, generate the PDF
      html2pdf().from(element).save('exported_file.pdf');
    } else {
      // Wait for the image to load and then generate the PDF
      imgElement.onload = () => {
        html2pdf().from(element).save('exported_file.pdf');
      };
    }
  };

  return (
    <div>
      <div ref={contentRef} className="main"> {/* Attach the ref to this div */}
        <h1>Export this content to PDF</h1>
        <div><img className='img' src={props.imgaddress}/></div>
      </div>
      <button onClick={exportHTMLtoPDF} className="butt">Export to PDF</button>
    </div>
  );
}

export default Pdf
