import React from 'react';
import './image.css';
import { useState } from 'react';

const Image = (props) => {
    const[style,setstyle]=useState("basic");
    const[option,setoption]=useState("")
    const[filters , setfilters] = useState(false);

    const handledelete = () =>{
        props.setoption(0);
    }

    const handlerotate = () => {
        setoption("")
        setstyle("rotate")
    }
    const handlefilters = () =>{
          setfilters(!filters)
    }

    const handleinvert = () =>{
        setoption("invert")
    }

    const handlesepia = () => {
      
            setoption("")
            setoption("sepia")
      
    }
    const handlebandw = () => {
        setoption("")
        setoption("bandw")
    }
  return (
    <div className='fullcontainer'>
    <div className='img'>
        <div className='imgfilter'>
     <img className={style+option} src={props.imgaddress}/>
     { filters==true ?
     <div className='filters'>
        <button ><img className="image" src={props.imgaddress}/></button>
        <button onClick={handlebandw}><img className="bandw" src={props.imgaddress}/></button>
<button><img className="lessbright" src={props.imgaddress}/></button>
<button><img className="bright" src={props.imgaddress}/></button>
<button onClick={handleinvert}><img className="invert" src={props.imgaddress}/></button>
<button onClick={handlesepia}><img className="sepia" src={props.imgaddress}/></button>

     </div>
     :
     <div></div>
}
     </div>
     <div className='btnn'>
     <button onClick={handlerotate}>Rotate</button> 
     <button>Crop</button>
     <button>Contrast</button>
     <button onClick={handlefilters}>Filters</button>
     <button onClick={handledelete}>Delete</button>
     </div>
     <div onClick={handledelete} className='btn'><button>Keep Scanning</button> <button className='pdfbtn'>Save PDF</button></div>
    </div>
    
    </div>
  )
}

export default Image
