import React, { useRef } from 'react';
import html2pdf from "html2pdf.js";
import './pdf.css'


const Pdf = () => {

  const contentRef = useRef(); // Create a ref

  const exportHTMLtoPDF = () => {
    const element = contentRef.current; // Access the component using the ref
    html2pdf().from(element).save('exported_file.pdf'); // Generate and save the PDF
  };

  return (
    <div>
      <div ref={contentRef} className="main"> {/* Attach the ref to this div */}
        <h1>Export this content to PDF
        <img className='img' src='https://buffer.com/library/content/images/size/w1200/2023/10/free-images.jpg'/>
        </h1>
      </div>
      <button onClick={exportHTMLtoPDF} className="butt">Export to PDF</button>
    </div>
  );
}

export default Pdf
