import Webcam from "react-webcam";
import { useCallback } from "react";
import { useState } from "react";
import React from "react";
import './WebcamCapture.css'
import Pdf from "./Pdf";
import useScreenSize from './useScreenSize';


const FACING_MODE_USER = "user";
const FACING_MODE_ENVIRONMENT = "environment";

const videoConstraints = {
  facingMode: FACING_MODE_USER
};

export const WebcamCapture = (props) => {
  const [facingMode, setFacingMode] = React.useState(FACING_MODE_USER);
 
  const screenSize = useScreenSize();
  console.log(screenSize.width);
    
  const handleClick = React.useCallback(() => {
    setFacingMode(
      prevState =>
        prevState === FACING_MODE_USER
          ? FACING_MODE_ENVIRONMENT
          : FACING_MODE_USER
    );
  }, []);
  
  const webcamRef = React.useRef(null);

  const [val,setval] = useState(false);
  const[imageUrl,setimageUrl]=useState("");
  const[pdf,setpdf]=useState(false);
  const[contrast,setcontrast]=useState(false);
console.log(pdf);
  const handleCapture = () =>{

    const imageSrc = webcamRef.current.getScreenshot();
    setimageUrl(imageSrc);
     setval(!val);
     
  }
  const Openimage = () =>{
    props.setoption(1);
  }
   const openContrast = () =>{
    setcontrast(!contrast);
   }

   const handlePdf = () => {
      setpdf(true);
   }
   props.setimgaddress(imageUrl);
  return (
    <div className="Container">
    <> 
    {pdf==true?
    
    <div>
      <Pdf/>
    </div>
    :
   <div>

    { val==false?

    <div className="cam">
      <Webcam
        audio={false}
        screenshotFormat="image/jpeg"
        width={320}
       
        ref={webcamRef}
        videoConstraints={{
          ...videoConstraints,
          facingMode
        }}
      />
      </div>


      :
      <div className="imgUrl"><img  src={imageUrl}/></div>
      
}        
<div className="capture">

      <button onClick={handleClick}><img className="rear" src="https://www.shutterstock.com/image-vector/switch-camera-icon-vector-symbol-260nw-2187122763.jpg"/></button>
      
      <button onClick={handleCapture} className="captur"><img src="https://static.vecteezy.com/system/resources/previews/007/522/948/original/camera-capture-icon-in-line-circle-button-vector.jpg"/></button>

      <button onClick={Openimage}><img className="image" src={imageUrl}/></button>
      </div>
      {/* { val==true?
      <button onClick={openContrast}>contrast</button>:
      <div></div>
}
{ contrast==true?
<div className="setcontrast"><button>-</button><button onClick={handleContrast}>+</button></div>
:
<div></div>

}
{ val==true?
      <button onClick={handlePdf}>Save As Pdf</button>:
      <div></div>
} */}
</div>
}
    </>
    </div>
  );
};
